(function($){$.fn.getCursorPosition=function(){var input=this.get(0);if(!input)return;if('selectionStart'in input){return input.selectionStart;}else if(document.selection){input.focus();var sel=document.selection.createRange();var selLen=document.selection.createRange().text.length;sel.moveStart('character',-input.value.length);return sel.text.length-selLen;}};$.fn.setCursorPosition=function(pos){var node=this.get(0);if(!node){return false;}else if(node.createTextRange){var textRange=node.createTextRange();textRange.collapse(true);textRange.moveEnd(pos);textRange.moveStart(pos);textRange.select();return true;}else if(node.setSelectionRange){node.setSelectionRange(pos,pos);return true;}};})(jQuery);